import "./App.css";
import React, { useState, useEffect } from "react";
import axios from "axios";
import { BrowserRouter as Router, Route, Routes, Navigate } from "react-router-dom";
import SearchBar from "./component/SearchBar.jsx";
import AddCar from "./component/AddCar.jsx";
import CarCard from "./component/CarCard.jsx";
import Login from "./component/Login.jsx";
import Register from "./component/Register.jsx";

function App() {
  const [cars, setCars] = useState([]);
  const [filteredCars, setFilteredCars] = useState([]);
  const [isAuthenticated, setIsAuthenticated] = useState(false);

  // Check if the user is authenticated on initial load
  useEffect(() => {
    checkAuth();
    fetchCars();
  }, []);

  const checkAuth = async () => {
    try {
      const response = await axios.get("http://localhost:5000/api/users/check-auth", {
        withCredentials: true, // Include cookies in the request
      });
      if (response.data.isAuthenticated) {
        setIsAuthenticated(true);
      }
    } catch (error) {
      console.error("Error checking authentication:", error);
    }
  };

  const fetchCars = () => {
    axios.get('http://localhost:5000/api/cars')
      .then(res => {
        setCars(res.data);
        setFilteredCars(res.data);
      })
      .catch((err) => console.error("Error fetching cars:", err));
  };

  const handleSearch = (searchTerm) => {
    const filtered = cars.filter(car =>
      car.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
      car.brand.toLowerCase().includes(searchTerm.toLowerCase())
    );
    setFilteredCars(filtered);
  };

  const handelAddCar = async (body) => {
    try {
      await axios.post("http://localhost:5000/api/cars/add", body, {
        withCredentials: true, // Include cookies in the request
      });
      fetchCars();
    } catch (error) {
      console.error("Error adding car:", error);
      alert("Failed to add car. Please check the data and try again.");
    }
  };

  const handelUpdate = (id, body) => {
    axios
      .put(`http://localhost:5000/api/cars/${id}`, body, {
        withCredentials: true, // Include cookies in the request
      })
      .then((res) => {
        console.log("updated successfully");
        fetchCars();
      })
      .catch((error) => {
        throw error;
      });
  };

  const handleDelete = async (carId) => {
    try {
      await axios.delete(`http://localhost:5000/api/cars/${carId}`, {
        withCredentials: true, // Include cookies in the request
      });
      fetchCars();
    } catch (err) {
      console.error("Error deleting car:", err);
    }
  };

  const handleLogin = async (username, password) => {
    try {
      const response = await axios.post(
        "http://localhost:5000/api/users/login",
        { username, password },
        { withCredentials: true } // Include cookies in the request
      );
      if (response.data.success) {
        setIsAuthenticated(true);
      }
    } catch (error) {
      console.error("Error logging in:", error);
      alert("Invalid username or password");
    }
  };

  const handleLogout = async () => {
    try {
      await axios.post("http://localhost:5000/api/users/logout", {}, {
        withCredentials: true, // Include cookies in the request
      });
      setIsAuthenticated(false);
    } catch (error) {
      console.error("Error logging out:", error);
    }
  };

  return (
    <Router>
      <div className="container">
        <div className="header-actions">
          <SearchBar onSearch={handleSearch} />
          {isAuthenticated ? (
            <button onClick={handleLogout}>Logout</button>
          ) : (
            <Navigate to="/login" />
          )}
        </div>

        <Routes>
          <Route
            path="/"
            element={
              isAuthenticated ? (
                <>
                  <AddCar handelAddCar={handelAddCar} />
                  <CarCard cars={filteredCars} handleDelete={handleDelete} handelUpdate={handelUpdate} />
                </>
              ) : (
                <Navigate to="/login" />
              )
            }
          />
          <Route path="/login" element={<Login onLogin={handleLogin} />} />
          <Route path="/register" element={<Register />} />
        </Routes>
      </div>
    </Router>
  );
}

export default App;
