import React,{useState} from 'react';
import './CarCard.css';
import UpdateCar from './updateCar.jsx';
function CarCard({ cars,handleDelete,handelUpdate }) {
    const [show,setShow]=useState(false)
    const changeShow=()=>{
        setShow(false)
    }

  return (
    <div className="car-card">
     {cars.map((el)=>{
        <div>
             <img src={el.image} alt={`${el.brand} ${el.model}`} />
         <h3>{el.brand} {el.model}</h3>
         <p>Year: {el.year}</p>
         <p>Price: ${el.price}</p>
         <p>{el.description}</p>
         <div className="car-card-actions">
           <button onClick={()=>changeShow(!show)}>Edit</button>
           {show&&<UpdateCar handelUpdate={handelUpdate} car={el}/>}
           <button onClick={()=>handleDelete(el.id)}>Delete</button>
         </div>
        </div>
     })}
    </div>
  );
}
