import "./App.css";
import React, { useState, useEffect } from "react";
import axios from "axios";
import CarList from "./component/CarList.jsx";
import SearchBar from "./component/SearchBar.jsx";
import CarForm from "./component/CarForm.jsx";

function App() {
  const [cars, setCars] = useState([]);
  const [filteredCars, setFilteredCars] = useState([]);
  const [showForm, setShowForm] = useState(false);
  const [editingCar, setEditingCar] = useState(null);

  useEffect(() => {
    fetchCars();
  }, []);

  const fetchCars = () => {
    axios.get('http://localhost:5000/api/cars')
      .then(res => {
        setCars(res.data);
        setFilteredCars(res.data);
      })
      .catch((err) => console.error("Error fetching cars:", err));
  };

  const handleSearch = (searchTerm) => {
    const filtered = cars.filter(car =>
      car.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
      car.brand.toLowerCase().includes(searchTerm.toLowerCase())
    );
    setFilteredCars(filtered);
  };

  const handleCarAdded = (newCar) => {
    axios.post("http://localhost:5000/api/cars/", newCar)
      .then((response) => {
        console.log("Car added successfully:", response.data);
        fetchCars(); // Refresh the car list
        setShowForm(false); 
      })
      .catch((error) => {
        console.error("Error adding car:", error);
      });
  };

  const handleEdit = (car) => {
    setEditingCar(car);
    setShowForm(true);
  };

  const handleDelete = async (carId) => {
    try {
      await axios.delete(`http://localhost:5000/api/cars/${carId}`);
      // Update the state to remove the deleted car
      setCars(prevCars => prevCars.filter(car => car._id !== carId));
      setFilteredCars(prevCars => prevCars.filter(car => car._id !== carId));
    } catch (err) {
      console.error("Error deleting car:", err);
    }
  };

  return (
    <div className="container">
      <div className="header-actions">
        <SearchBar onSearch={handleSearch} />
        <button
          className="add-car-button"
          onClick={() => setShowForm(true)}
        >
          Add New Car
        </button>
      </div>

      {showForm && (
        <CarForm
          onCarAdded={handleCarAdded}
          editCar={editingCar}
          onCancel={() => {
            setShowForm(false);
            setEditingCar(null);
          }}
        />
      )}

      <CarList
        cars={filteredCars}
        onEdit={handleEdit}
        onDelete={handleDelete}
      />
    </div>
  );
}

export default App;
