import React, { useState } from 'react';
import axios from "axios";
import './UpdateCar.css';

function UpdateCar({ car, fetchCars, onCancel }) {
  const [updatedCar, setUpdatedCar] = useState({
    brand: car.brand,
    model: car.model,
    year: car.year,
    price: car.price,
    description: car.description,
    image: car.image
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setUpdatedCar({ ...updatedCar, [name]: value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    handelUpdate(car.id, updatedCar);
  };

  const handelUpdate = (id, body) => {
    axios
      .put(`http://localhost:5000/api/cars/${id}`, body)
      .then((res) => {
        console.log("updated successfully");
        fetchCars();
        onCancel(); // Hide the form after updating a car
      })
      .catch((error) => {
        console.error("Error updating car:", error);
      });
  };

  return (
    <div className="update-car-form">
      <form onSubmit={handleSubmit}>
        <input type="text" name="brand" placeholder="Brand" value={updatedCar.brand} onChange={handleChange} required />
        <input type="text" name="model" placeholder="Model" value={updatedCar.model} onChange={handleChange} required />
        <input type="number" name="year" placeholder="Year" value={updatedCar.year} onChange={handleChange} required />
        <input type="number" name="price" placeholder="Price" value={updatedCar.price} onChange={handleChange} required />
        <input type="text" name="description" placeholder="Description" value={updatedCar.description} onChange={handleChange} required />
        <input type="text" name="image" placeholder="Image URL" value={updatedCar.image} onChange={handleChange} required />
        <button type="submit">Update Car</button>
        <button type="button" onClick={onCancel}>Cancel</button>
      </form>
    </div>
  );
}

export default UpdateCar;
