import React,{useState} from 'react'

function UpdateCar({handelUpdate,car}) {
const [brand,setBrand]=useState(car.brand)
const [model,setModel]=useState(car.model)
const [year,setYear]=useState(car.year)
const [price,setPrice]=useState(car.price)
const [description,setDescription]=useState(car.description)
const [image,setImage]=useState(car.image)





  return (
    <div className="add-car-form">
      <form onSubmit={handleSubmit}>
        <input type="text" name="brand" placeholder="Brand" defaultValue={brand} onChange={(e)=>setBrand(e.target.value)} required />
        <input type="text" name="model" placeholder="Model" defaultValue={model} onChange={(e)=>setModel(e.target.value)} required />
        <input type="number" name="year" placeholder="Year" defaultValue={year} onChange={(e)=>setYear(e.target.value)} required />
        <input type="number" name="price" placeholder="Price" defaultValue={price} onChange={(e)=>setPrice(e.target.value)} required />
        <input type="text" name="description" placeholder="Description" defaultValue={description} onChange={(e)=>setDescription(e.target.value)} required />
        <input type="text" name="image" placeholder="Image URL" defaultValue={image} onChange={(e)=>setImage(e.target.value)} required />
        <button type="submit" onClick={()=>handelUpdate(car.id,{brand,model,year,price,description,image})}>Add Car</button>
      </form>
    </div>
  );



}

export default UpdateCar
