// 



function App() {
  const [cars, setCars] = useState([]);
  const [filteredCars, setFilteredCars] = useState([]);

  const fetchCars = () => {
    axios.get('http://localhost:5000/api/cars')
      .then(res => {
        setCars(res.data);
        setFilteredCars(res.data); // Ensure filteredCars is updated
      })
      .catch((err) => console.error("Error fetching cars:", err));
  };

  const handleSearch = (searchTerm) => {
    const filtered = cars.filter(car =>
      car.brand.toLowerCase().includes(searchTerm.toLowerCase()) ||
      car.model.toLowerCase().includes(searchTerm.toLowerCase())
    );
    setFilteredCars(filtered);
  };

  const handelAddCar = async (body) => {
    try {
      await axios.post("http://localhost:5000/api/cars/add", body, {
        headers: {
          "Content-Type": "application/json",
        },
      });
      fetchCars(); // This will update both cars and filteredCars
    } catch (error) {
      console.error("Error adding car:", error);
      alert("Failed to add car. Please check the data and try again.");
    }
  };

  const handelUpdate = (id, body) => {
    axios
      .put(`http://localhost:5000/api/cars/${id}`, body)
      .then((res) => {
        console.log("updated successfully");
        fetchCars(); // This will update both cars and filteredCars
      })
      .catch((error) => {
        throw error;
      });
  };

  const handleDelete = async (carId) => {
    try {
      await axios.delete(`http://localhost:5000/api/cars/${carId}`);
      fetchCars(); // This will update both cars and filteredCars
    } catch (err) {
      console.error("Error deleting car:", err);
    }
  };

  useEffect(() => {
    fetchCars();
  }, []);

  return (
    <div className="container">
      <div className="header-actions">
        <SearchBar onSearch={handleSearch} />
      </div>

      <AddCar handelAddCar={handelAddCar} />
      
      <CarCard cars={filteredCars} handleDelete={handleDelete} handelUpdate={handelUpdate} />
    </div>
  );
}

export default App;
