import React, { useState } from 'react';
import axios from 'axios';
import './CarForm.css';

function CarForm({ onCarAdded, editCar, onCancel }) {
  const initialState = editCar || {
    brand: '',
    model: '',
    year: '',
    price: '',
    description: '',
    image: ''
  };

  const [formData, setFormData] = useState(initialState);
  const [error, setError] = useState('');

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');

    try {
      if (editCar) {
        // Update car
        const response = await axios.put(
          `http://localhost:5000/api/cars/${editCar.id}`,
          formData
        );
        onCarAdded(response.data);
      } else {
        // Add new car
        const response = await axios.post(
          'http://localhost:5000/api/cars',
          formData
        );
        onCarAdded(response.data);
      }

      // Reset form and close
      setFormData(initialState);
      if (onCancel) onCancel();
    } catch (err) {
      // console.error("Error submitting form:", err.response?.data); // Log the error response
      setError(err.response?.data?.message || 'Error submitting form');
    }
  };

  return (
    <div className="car-form-container">
      <form onSubmit={handleSubmit} className="car-form">
        <h2>{editCar ? 'Edit Car' : 'Add New Car'}</h2>
        
        {error && <div className="error-message">{error}</div>}
        
        <div className="form-group">
          <label htmlFor="brand">Brand</label>
          <input
            type="text"
            id="brand"
            name="brand"
            value={formData.brand}
            onChange={handleChange}
            required
            placeholder="Enter car brand"
          />
        </div>

        <div className="form-group">
          <label htmlFor="model">Model</label>
          <input
            type="text"
            id="model"
            name="model"
            value={formData.model}
            onChange={handleChange}
            required
            placeholder="Enter car model"
          />
        </div>

        <div className="form-group">
          <label htmlFor="year">Year</label>
          <input
            type="number"
            id="year"
            name="year"
            value={formData.year}
            onChange={handleChange}
            required
            placeholder="Enter car year"
          />
        </div>

        <div className="form-group">
          <label htmlFor="price">Price</label>
          <input
            type="number"
            id="price"
            name="price"
            value={formData.price}
            onChange={handleChange}
            required
            placeholder="Enter car price"
          />
        </div>

        <div className="form-group">
          <label htmlFor="image">Image URL</label>
          <input
            type="url"
            id="image"
            name="image"
            value={formData.image}
            onChange={handleChange}
            required
            placeholder="Enter image URL"
          />
        </div>

        <div className="form-group">
          <label htmlFor="description">Description</label>
          <textarea
            id="description"
            name="description"
            value={formData.description}
            onChange={handleChange}
            required
            placeholder="Enter car description"
            rows="4"
          />
        </div>

        <div className="form-buttons">
          <button type="submit" className="submit-button">
            {editCar ? 'Update Car' : 'Add Car'}
          </button>
          {onCancel && (
            <button 
              type="button" 
              onClick={onCancel}
              className="cancel-button"
            >
              Cancel
            </button>
          )}
        </div>
      </form>
    </div>
  );
}

export default CarForm;
