import React from 'react';

function CarCard({ cars, handleDelete, handleUpdate }) {
  return (
    <div className="car-card-container">
      {cars.map(car => (
        <div key={car._id} className="car-card">
          <img src={car.image} alt={car.name} />
          <h2>{car.name}</h2>
          <p>{car.brand}</p>
          <p>{car.year}</p>
          <p>${car.price}</p>
          <p>{car.description}</p>
          <button onClick={() => handleDelete(car._id)}>Delete</button>
          <button onClick={() => handleUpdate(car._id, car)}>Update</button>
        </div>
      ))}
    </div>
  );
}

export default CarCard;
