import "./App.css";
import React, { useState, useEffect } from "react";
import axios from "axios";
import CarList from "./component/CarList.jsx";
import SearchBar from "./component/SearchBar.jsx";
import CarForm from "./component/CarForm.jsx";

function App() {
  const [cars, setCars] = useState([]);
  const [filteredCars, setFilteredCars] = useState([]);
  const [showForm, setShowForm] = useState(false);
  const [editingCar, setEditingCar] = useState(null);
  const [brand, setBrand] = useState("");
  const [model, setModel] = useState("");
  const [year, setYear] = useState("");
  const [price, setPrice] = useState("");
  const [description, setDescription] = useState("");
  const [image, setImage] = useState("");

  useEffect(() => {
    fetchCars();
  }, []);

  const fetchCars = () => {
    axios.get('http://localhost:5000/api/cars')
      .then(res => {
        setCars(res.data);
        setFilteredCars(res.data);
      })
      .catch((err) => console.error("Error fetching cars:", err));
  };

  const handleSearch = (searchTerm) => {
    const filtered = cars.filter(car =>
      car.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
      car.brand.toLowerCase().includes(searchTerm.toLowerCase())
    );
    setFilteredCars(filtered);
  };

  const handleCarAdded = (e) => {
    e.preventDefault();
    const newCar = { brand, model, year, price, description, image };
    axios.post("http://localhost:5000/api/cars/", newCar)
      .then((response) => {
        console.log(response.data);
        setBrand("");
        setModel("");
        setYear("");
        setPrice("");
        setDescription("");
        setImage("");
        fetchCars();
      })
      .catch((error) => {
        console.error("Error adding car:", error);
      });
  };

  const handleEdit = (car) => {
    setEditingCar(car);
    setShowForm(true);
  };

  const handleDelete = async (carId) => {
    try {
      await axios.delete(`http://localhost:5000/api/cars/${carId}`);
      fetchCars();
    } catch (err) {
      console.error("Error deleting car:", err);
    }
  };

  return (
    <div className="container">
      <div className="header-actions">
        <SearchBar onSearch={handleSearch} />
        <button
          className="add-car-button"
          onClick={() => setShowForm(true)}
        >
          Add New Car
        </button>
      </div>

      {showForm && (
        <CarForm
          onCarAdded={handleCarAdded}
          editCar={editingCar}
          onCancel={() => {
            setShowForm(false);
            setEditingCar(null);
          }}
        />
      )}

      <CarList
        cars={filteredCars}
        onEdit={handleEdit}
        onDelete={handleDelete}
      />
    </div>
  );
}

export default App;
